INSERT INTO `news_base` VALUES ('142', '湖地海〔2016〕9号：关于湖州市妙湖线航道里鱼山矿码头下游约250米航段临时钢栈桥和大桥下部结构施工的航行通告', '<p><br></p><p style=\"margin: 0px; padding: 0px; border: 0px currentColor; border-image: none; text-align: left; color: rgb(51, 51, 51); text-transform: none; line-height: 18px; text-indent: 0px; letter-spacing: normal; font-size: 12px; font-style: normal; font-variant: normal; font-weight: normal; word-spacing: 0px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); -webkit-text-stroke-width: 0px;\"><span style=\"margin: 0px; padding: 0px; border: 0px currentColor; border-image: none; font-family: 仿宋_GB2312; font-size: 12px;\"><span style=\"margin: 0px; padding: 0px; border: 0px currentColor; border-image: none; font-size: 15pt;\"><font face=\"宋体\">各有关单位、船舶：</font></span></span></p><p><br></p><div style=\"margin: 0px; padding: 0px; border: 0px currentColor; border-image: none; text-align: left; color: rgb(51, 51, 51); text-transform: none; line-height: 35pt; text-indent: 30pt; letter-spacing: normal; font-size: 12px; font-style: normal; font-variant: normal; font-weight: normal; word-spacing: 0px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); -webkit-text-stroke-width: 0px;\"><span style=\"margin: 0px; padding: 0px; border: 0px currentColor; border-image: none; font-size: 12px;\"><span style=\"margin: 0px; padding: 0px; border: 0px currentColor; border-image: none; font-size: 15pt;\"><font face=\"宋体\">因湖州经济技术开发区管理委员会新建环漾大桥的需要，由中铁隧道集团有限公司在湖州市妙湖线航道里鱼山矿码头下游约250米航段进行临时钢栈桥和大桥下部结构施工，为确保工程施工和船舶航行安全，现将有关事项通告如下：</font></span></span></div><p><br></p><div style=\"margin: 0px; padding: 0px; border: 0px currentColor; border-image: none; text-align: left; color: rgb(51, 51, 51); text-transform: none; line-height: 35pt; text-indent: 30pt; letter-spacing: normal; font-size: 12px; font-style: normal; font-variant: normal; font-weight: normal; word-spacing: 0px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); -webkit-tex