/**
 * 定位底部导航栏
 */
function setLinklineDivPos(){
	var linkdivid = document.getElementById("linklineid");
	linkdivid.style.top =  getBottomPos("traffectedroad");
	var linkleft = document.getElementById("link_Left");
	linkleft.style.left = getLeftPos("linklineid");
	linkleft.style.top = getBottomPos("traffectedroad");
}

/**
 * 底部导航栏加链接与onpress事件
 */
function addLinkToGuid(){
	initGuildPic();
	
	$("#link_Left").click(function(){
		$("#linklineid").toggle();
	});
	$("#mainguid_traff").click(function (){
		window.location.href= $("#basePath").val()+"page/main/traffic.jsp";
	});
	$("#mainguid_road").click(function (){
		window.location.href= $("#basePath").val()+"page/main/road.jsp";
	});
	$("#mainguid_transport").click(function (){
		window.location.href= $("#basePath").val()+"page/main/transport.jsp";
	});
	$("#mainguid_outgo").click(function (){
		window.location.href= $("#basePath").val()+"page/port/ship.jsp";
	});
	
}

/**
 * 获取某个元素最底部15pm的位置
 */
function getBottomPos(elementId){
	var el =  document.getElementById(elementId);
	var setTop = el.offsetHeight +15;

//	var setTop = el.offsetTop + el.offsetHeight +15;
	return setTop+"px";
}
function getLeftPos(elementId){
	var el =  document.getElementById(elementId);
	var setLeft = el.offsetLeft -30;

//	var setTop = el.offsetTop + el.offsetHeight +15;
	return setLeft+"px";
}

