/**
 * 颜色设置
 * @param tpi
 */
function tpiColor(tpi){
	var color;
	if (tpi <2) {
		color= 1;
	} else if (tpi <4) {
		color =2;
	} else if (tpi <6) {
		color = 3;
	} else if (tpi <8) {
		color = 4;
	} else {
		color =5;
	}
	return color;
}

function conColor(level){
	var color;
	if (level =='畅通') {
		color= 1;
	} else if (level = '基本畅通') {
		color =2;
	} else if (level = '轻度拥堵') {
		color = 3;
	} else if (level = '中度拥堵') {
		color = 4;
	} else {
		color =5;
	}
	return color;
}

