function mapColor(tpi){
	var color;
	if (tpi <2) {
		color = "#00B300";
	} else if (tpi <4) {
		color = "#64C97B";
	} else if (tpi < 6) {
		color = "#FFC600";
	} else if (tpi < 8) {
		color = "#FF5A00";
	} else {
		color = "#FE0000";
	}
	return color;
}

